        //IntPtr類型稱為「平台特定的整數類型」
        //Win32API時，一定要按照WinAPI的原型來聲明
        IntPtr _ClipboardViewerNext;
        private void RegisterClipboardViewer()
        {
            _ClipboardViewerNext = Win32.User32.SetClipboardViewer(this.Handle);
        }

        private void UnregisterClipboardViewer()
        {
            Win32.User32.ChangeClipboardChain(this.Handle, _ClipboardViewerNext);
        }
        protected override void WndProc(ref Message m)
        {
            switch ((Win32.Msgs)m.Msg)
            {
                case Win32.Msgs.WM_DRAWCLIPBOARD:
                    {
                        if (Clipboard.ContainsText())
                        {

                            sendToUrlAsync();
                            if ((Clipboard.GetText()).Length > 20)
                            {
                                current_label.Text = (Clipboard.GetText()).Substring(0, 20) + "...";

                            }
                            else
                            {
                                current_label.Text = Clipboard.GetText();
                            }


                        }
                        //顯示剪貼簿中的圖片資訊
                        if (Clipboard.ContainsImage())
                        {
                            current_label.Text = "圖片";

                        }
                        //MessageBox.Show("接收到了");
                        //把剪貼簿在傳給下一個剪貼簿鏈的程式
                        Win32.User32.SendMessage(_ClipboardViewerNext, m.Msg, m.WParam.ToInt32(), m.LParam.ToInt32());

                        break;
                    }
                case Win32.Msgs.WM_CHANGECBCHAIN:
                    {
                        if (m.WParam == _ClipboardViewerNext)
                        {
                            //
                            // If wParam is the next clipboard viewer then it
                            // is being removed so update pointer to the next
                            // window in the clipboard chain
                            //
                            _ClipboardViewerNext = m.LParam;
                        }
                        else
                        {
                            Win32.User32.SendMessage(_ClipboardViewerNext, m.Msg, m.WParam.ToInt32(), m.LParam.ToInt32());
                        }
                        break;
                    }
                default:
                    {
                        base.WndProc(ref m);
                        break;
                    }
            }
        }
        
        public string cleanText()
        {
            if (Clipboard.ContainsText(TextDataFormat.Html))
            {
                MemoryStream vMemoryStream =
                Clipboard.GetData("Html Format") as MemoryStream;
                vMemoryStream.Position = 0;
                byte[] vBytes = new byte[vMemoryStream.Length];
                vMemoryStream.Read(vBytes, 0, (int)vMemoryStream.Length);
                
                return Encoding.UTF8.GetString(vBytes).ToString();
            }
            return Clipboard.GetText();
        }
        public async Task sendToUrlAsync()
        {
            DateTime localDate = DateTime.Now;
            HttpClient client = new HttpClient();
            var values = new Dictionary<string, string>
                            {
                                  { "ID", user },
                                  { "Msg", Clipboard.GetText() },
                                  {"Source","PC"},
                                  {"Time", localDate.ToString("yyyy/MM/dd hh:mm:ss") }
                            };
            var data = new FormUrlEncodedContent(values);
            var response = await client.PostAsync(sendurl, data);
        }
